import React, { useState } from 'react';
import './Modal.css';

const ConfirmationModal = ({ closeModal }) => {
  const [showDetails, setShowDetails] = useState(false);

  return (
    <div className="ModalOverlay">
      <div className="ConfirmationModal">
        <h2>Order Submitted! </h2>
        <p>Thank you for shopping with us. Your order is on its way!</p>
        <button onClick={closeModal} className="PrimaryButton">OK</button>
        {showDetails && (
          <div className="OrderDetails">
            <h3>Order Details</h3>
            {/* Display order summary information here */}
          </div>
        )}
        <button onClick={() => setShowDetails(!showDetails)} className="SecondaryButton">
          {showDetails ? 'Hide Details' : 'Show Details'}
        </button>
      </div>
    </div>
  );
};

export default ConfirmationModal;
